// Flutterwave sends verif-hash header (direct comparison, not HMAC)
const headers = $json.headers || {};
const receivedHash = headers['verif-hash'] || headers['Verif-Hash'] || headers['VERIF-HASH'];
const expectedHash = $env.FLW_VERIF_HASH;

if (!receivedHash) {
  return [{
    json: {
      valid: false,
      reason: 'missing_hash',
      body: $json.body || $json
    }
  }];
}

if (receivedHash !== expectedHash) {
  return [{
    json: {
      valid: false,
      reason: 'invalid_hash',
      received: receivedHash,
      body: $json.body || $json
    }
  }];
}

return [{
  json: {
    valid: true,
    body: $json.body || $json
  }
}];

const body = $json.body || {};
const data = body.data || body;

// Extract payment details
const payment = {
  transaction_id: data.id || data.txRef || data.tx_ref,
  flw_ref: data.flwRef || data.flw_ref,
  amount: parseFloat(data.amount || 0),
  currency: data.currency || 'USD',
  status: (data.status || '').toLowerCase(),
  customer_email: data.customer?.email || data.email,
  customer_name: data.customer?.name || data.name,
  customer_phone: data.customer?.phone_number || data.phone,
  payment_type: data.payment_type,
  created_at: data.created_at || new Date().toISOString(),
  raw_data: JSON.stringify(data)
};

// Determine plan type based on amount
if (payment.amount >= 500) {
  payment.plan = 'pro';
  payment.plan_name = 'Pro Setup';
} else if (payment.amount >= 300) {
  payment.plan = 'starter';
  payment.plan_name = 'Starter Setup';
} else {
  payment.plan = 'unknown';
  payment.plan_name = 'Unknown';
}

return [{ json: payment }];

// Log security incident
const incident = {
  timestamp: new Date().toISOString(),
  reason: $json.reason,
  received_hash: $json.received,
  ip: $json.headers?.['x-forwarded-for'] || 'unknown',
  body: $json.body
};

console.error('SECURITY: Invalid webhook signature', incident);

// Send alert email
return [{ json: incident }];

// Log failed/pending payment
const payment = $node['Parse Payment Data'].json;
const verification = $json.data;

console.log(`Payment not successful: ${payment.transaction_id} - Status: ${verification.status}`);

return [{
  json: {
    transaction_id: payment.transaction_id,
    status: verification.status,
    customer_email: payment.customer_email,
    amount: payment.amount
  }
}];
